import React from 'react';
import '../Style/MainStep.css';
import Story from './Story';
import Media from './Media';
import Music from './Music';

class MainStep extends React.Component{
    state={
        type:null
    }

    getDisplayData = (event) => {
        console.log(event.target.id);
        this.setState({type : event.target.id});
    }

    getDisplayDetails = () => {
        if(this.state.type === 'Story')    
        {
            return <Story/>
        }
        if(this.state.type === 'Media'){
            
            return <Media />
        }
        if(this.state.type === 'Music'){
            return <Music />
        }
    }

    render(){
        return (
            <div>
                <div className='container'>
                    <div><button id = 'Story' onClick = {this.getDisplayData}>Story</button></div>
                    <div><button id = 'Media' onClick = {this.getDisplayData}>Media</button></div>
                    <div><button id = 'Music' onClick = {this.getDisplayData}>Music</button></div>
                </div>
                <div>{this.getDisplayDetails()}</div>
            </div>
        );

    }
}

export default MainStep;
