import React from 'react';
import {getMediaData} from '../Services/MediaDetails'
import VideoItem from './VideoItem';

var renderList;

class Media extends React.Component{
    state ={
        dataToDisplay: []
    }

    async componentDidMount() {
        console.log('mounted');
        var dataFromApi = await getMediaData();
        this.setState({ dataToDisplay: dataFromApi });
    }

    componentDidUpdate(){
        console.log('updated');
        console.log(this.state.dataToDisplay);
        //renderList = this.state.dataToDisplay.map(videoContent => {
        //    console.log(videoContent);
        //    return <VideoItem />
       // });
    }

    render(){
        return(
            <div className='content-box-media'>
                {this.props.dataToDisplayInMedia}
            </div>
        );
    }
}

export default Media;
