import React from 'react';
import '../Style/MainStep.css';
import Story from './Story';

class MainStep extends React.Component{
    state={
        type:'Story'
    }

    getDisplayData = (type) => {
        this.setState({type : type});
    }

    getDisplayDetails = () => {
        if(this.state.type === 'Story')    
        {
            return <Story/>
        }
    }

    render(){
        return (
            <div>
                <div className='container'>
                    <div><button onClick = {this.getDisplayData('Story')}>Story</button></div>
                    <div><button>Media</button></div>
                    <div><button>Music</button></div>
                </div>
                <div>{this.getDisplayDetails}</div>
            </div>
        );

    }
}

export default MainStep;
